mapper.select:

select * from tbl_product

where pname like concat('%',#{keyword},'%')




where price <= #{keyword}





where price >= #{keyword}



 order by pcode desc 
 limit #{pageStart},#{perPageNum}

 select count(*) from tbl_product 

where pname like concat('%',#{keyword},'%')




where price <= #{keyword}





where price >= #{keyword}
